/*global location */
sap.ui.define([
	"sitbh/controller/BaseController",
	"sitbh/model/formatter"
], function(BaseController, formatter) {
	"use strict";
	return BaseController.extend("sitbh.controller.Produtos", {
		formatter: formatter,
		onInit: function() {
			
			this._oRouter = this.getRouter();
			this._oRouter.getRoute("categoriaEscolhida").attachPatternMatched(this._onPatternMatched, this);
		},
		/**
		 *@memberOf sitbh.controller.Produtos
		 */
		onBack: function() {
			//This code was generated by the layout editor.
			this._oRouter.navTo("default");
		},
		
		_onPatternMatched: function(oEvent){
			// debugger;
			var oParameters = oEvent.getParameters();
			var oArguments = oParameters.arguments;
			var sIdCategoria = oArguments.id;
			this.getView().bindElement("/Categories(" + sIdCategoria + ")");
		}
	});
});